import { render } from '@testing-library/react';
import React, { Component } from 'react'; 
import firebase, {db, tasksCollection, firebaseTimestamp} from '../utils/firebase';
import { firebaseArrMaker } from '../utils/tools';

class Search extends Component{
    state = {
        searchParams: ""
    }

    handleChange = (params) => {
        this.setState({searchParams: params.target.value},()=>console.log(this.state.searchParams));
    }

    handleSearch = (e) => {
        e.preventDefault();
        var filteredList = this.props.taskList.filter( item => item.title.toLowerCase().includes(this.state.searchParams.toLowerCase())
                   || item.details.toLowerCase().includes(this.state.searchParams.toLowerCase()));
        console.log(filteredList);
        this.props.displaySearch(filteredList, this.state.searchParams);
        this.setState({searchParams: ""});
    }

    render(){
        return(
            <div className="searchDiv">
                <div className="searchDiv-center">
                    <form id="searchContainer" className="form-group" onSubmit={this.handleSearch}> 
                        <input 
                                type="text" 
                                id="searchInput" 
                                className="form-control searchBar" 
                                value={this.state.searchParams}
                                placeholder="Search for a word or phrase"
                                onChange={(e)=>this.handleChange(e)} 
                                required
                        >
                        </input>
                        {/* <div>
                            <button type="submit" id="searchSubmit" className="form-control taskTitle btn btn-secondary">
                                <img className="searchIcon" src="data:image/png;base64,"/>
                            </button>
                        </div> */}
                    </form>
                </div>
            </div>
        )
    }
}

export default Search;